<script>
import{ mapGetters, mapMutations } from 'vuex';
import List from './List';
import mappedText from './mappedText';


export default {
  name:"Contributor",
  components: { List, mappedText },
  data() { return {
    headerEl: null,
    headerTop: 0,
      header20: false,
    SCROLL: 0,
  }},
  computed: {
    ...mapGetters([
      'VERSION', 
      'BUILD', 
      'VIEWTYPE', 
      'byType', 
      'SIGNS'
    ]),

    HEADER_SCROLL: function(){
      return this.SCROLL / this.headerTop
    },

    listOp: function(){
      if(this.header20){
        return {  
          'transition': '600ms',
          'transition-timing-function': 'cubic-bezier(.71,.19,.66,.97)',
          opacity: '0.88'
        }
      }else{
        return {
          transition: '300ms',
          opacity: '0.46'
        }
      }
    }

  },
  watch: {
    
    HEADER_SCROLL(nu, old){
      if(this.VIEWTYPE !== 'wide'){
        if(nu > 0.8){
          this.header20 = true
        }else if(nu > 0.2){
          this.header20 = true
        }else{
          this.header20 = false
        }
      }else{
        return old
      }
    },

  },
  methods: {
    ...mapMutations(['pathAgain']),

    getCoords(elem) {
      var box = elem.getBoundingClientRect();
      var body = document.body;
      var docEl = document.documentElement;
      var scrollTop = window.pageYOffset || docEl.scrollTop || body.scrollTop;
      var clientTop = docEl.clientTop || body.clientTop || 0;
      var top  = box.top +  scrollTop - clientTop;
      return Math.round(top)
    },

    handleScroll(){
      this.SCROLL = window.scrollY;
    },

  },
  created() {

    window.addEventListener('scroll', this.handleScroll)

  },
  mounted() {

    this.headerEl = document.querySelector('.header');
    this.headerTop = this.getCoords(this.headerEl);
    if(this.VIEWTYPE === 'wide'){
      this.header20 = true;
    }

  }
}
</script>


