  <transition name="writer-appear">
  <div class="pathmaker-wrapper"
  v-if="(elSequence > 4) && !WRITER_DONE">
    <div class="pm" :class="byType">
      <Pathmaker/>
    </div>
  </div>
  </transition>

  <transition name="writer-appear">
  <div class="btn-wrapper" 
  v-if="(elSequence === 5) && (pathBtns)">
    <div class="bw" :class="byType">
      <div class="btn left"
        @click="UNDO_PATH()"
        >undo</div>
      <div class="btn right"
        @click="DONE()"
        >done</div>
    </div>
  </div>
  </transition>

  <transition name="writer-des-appear">
    <InfoGetter 
      v-if="(SEQ === 3)" 
      @toggle-submit-visibility="toggleSubmitVis"
    />
  </transition>

  <transition name="writer-appear">
  <div class="btn-wrapper" 
  v-if="(SEQ === 3)&&(submitBtn)">
    <div class="bw" :class="byType">
      <div class="btn one"
        @click="SEND_PATHS"
        >submit</div>
    </div>
  </div>
  </transition>
  
</div></div>
</template>



<script>
import { mapState, mapGetters, mapMutations } from 'vuex';
import Pathmaker from './Pathmaker';
import InfoGetter from './InfoGetter';

const name = 'Writer';
export default {
  name,
  components: { Pathmaker, InfoGetter },
  data() { return {
    suspension: null,
    elSequence: 0,
    pathBtns: false,
    submitBtn: false
  }},
  computed: {
    ...mapState(['aniTiming', 'blocks']),
    ...mapGetters([
        'TC',
        'byType', 
        'VIEWTYPE', 
        'SEQ', 
        'bs',
        'wOff',
        'NEW_PATHS', 
        'WRITER_DONE'
      ]),
    AT: function(){
      return this['aniTiming'][name]
    },
    wrapper: function(){
      if(this.SEQ === 2){
        if(this.VIEWTYPE === 'small'){
          return {
            'height': 'calc(100vh - 22vw)',
          }
        }else if(this.VIEWTYPE === 'narrow'){
          return {
            'height': 'calc(100vh - 22vw)',
          }
        }else if(this.VIEWTYPE === 'tablet'){
          return {
            'height': 'calc(100vh - 20vw)',
          }
        }else{
          return { // this.VIEWTYPE === 'wide'
            'height': 'calc(100vh - 5vw)',
          }
        }
      }else{
        return { 'height': 0 }
      }
    },

  },
  watch: {
    NEW_PATHS(nu, old){
      if(nu === 0){
        this.pathBtns = false;
      }else{
        this.pathBtns = true;
        return old
      }
    },
    WRITER_DONE(nu, old){
      if(nu){
        this.pathBtns = false;
      }else{
        return old
      }
    }
  },
  methods: {
    ...mapMutations(['UNDO_PATH', 'SEND_PATHS', 'moveTo']),
    proceed(){
      this.elSequence += 1;
      if(this.elSequence < 5){
        setTimeout(this.proceed, this.AT.elAppear);
      }
    },
    DONE(){
      this.$store.state.writerDone = true;
      if(this.TC.testSequence){
        this.$store.state.test.client.sequenceNow = 3;
      }else{
        this.moveTo(3);
      }
    },
    toggleSubmitVis(bool){
      this.submitBtn = bool;
    }
  },
  mounted() {
    setTimeout(this.proceed, this.AT.startElements);
  },
}
</script>



